var encryption = require('./sodium')

var secretKey = Buffer('1d81a0cefe79e8dd4b0c088db6a5d26d35a6be5989cbf37d09a3ca65c07668c1', 'hex')
var keyPair = encryption.scalarMultiplicationKeyPair(secretKey)

var otherSecretKey = Buffer('d99a307b252473d19b878eef06cb179c7e96cb749069b513407d1283aa06ab87', 'hex')
var otherKeyPair = encryption.scalarMultiplicationKeyPair(otherSecretKey)

console.log('key pair:', keyPair)
console.log('other key pair:', otherKeyPair)

console.log('shared', encryption.scalarMultiplication(secretKey, otherKeyPair.publicKey))
console.log('shared', encryption.scalarMultiplication(otherSecretKey, keyPair.publicKey))
